script:
frappe.ui.form.on("Sales Invoice", "kirat_invoice_type", function(frm) {
  switch (frm.doc.kirat_invoice_type) {
     case "Supplementary Invoice":
        frm.set_value("naming_series", "SINV-SUP-");
        break;
     case "Invoice for Sample":
        frm.set_value("naming_series", "SINV-SMP-");
        break;
     case "Challan":
        frm.set_value("naming_series", "SINV-CHL-");
        break;
     default:
        frm.set_value("naming_series", "SINV-");
        break;
  }
});

//Disable Invoice type selection in modify mode.
frappe.ui.form.on("Sales Invoice", "refresh", function(frm) {
    // use the __islocal value of doc, to check if the doc is saved or not
    frm.set_df_property("kirat_invoice_type", "read_only", frm.doc.__islocal ? 0 : 1);
});
